let n = Number(new Date().getFullYear());
let years = [];
for (i=n; i > (n-120); i--) {
	years.push({"label":i,"value":i});
}
cti.store.variables.yearofbirth = years;

let shocks = [];
for (let s=1; s < 21; s++) {
	shocks.push({"label":s,"value":s});
}
cti.store.variables.shocks = shocks;

let bgl = [];
for (let b=0; b <= 50; b = b + 0.5) {
	bgl.push({"label":b,"value":b});
}
cti.store.variables.bgl = bgl;

let pulse = [];
for (let p=28; p <= 221; p++) {
	pulse.push({"label":p,"value":p});
}
cti.store.variables.pulse = pulse;

let rr = [];
for (let r=0; r <= 100; r++) {
	rr.push({"label":r,"value":r});
}
cti.store.variables.respiratoryrate = rr;

cti.store.variables.oxygensats = rr.slice(0).reverse();

let temps = [];
for (let t=28; t <= 44.1; t = t + 0.1) {
	temps.push({"label":t.toFixed(1),"value":t.toFixed(1)});
}
cti.store.variables.temperatures = temps;

let dia = [];
for (let d=60; d <= 141; d++) {
	dia.push({"label":d,"value":d});
}
cti.store.variables.diastolic = dia;

let sys = [];
for (let s=90; s <= 251; s++) {
	sys.push({"label":s,"value":s});
}
cti.store.variables.systolic = sys;

